package com.ionicframework.IdentityVault;

import android.app.Dialog;
import android.content.Context;
import android.os.Handler;
import android.view.MotionEvent;
import android.view.Window;
import androidx.annotation.NonNull;

public class PrivacyScreenDialog extends Dialog {

    public PrivacyScreenDialog(@NonNull Context context) {
        super(context, android.R.style.Theme_Black);
        requestWindowFeature(Window.FEATURE_NO_TITLE);
    }

    @Override
    public boolean dispatchTouchEvent(MotionEvent ev) {
        this.dismiss();
        return super.dispatchTouchEvent(ev);
    }

    @Override
    public void onWindowFocusChanged(boolean hasFocus) {
        this.hide();

        // Dismiss must run on next loop to avoid race condition
        Handler handler = new Handler();
        handler.postDelayed(this::dismiss, 0);
    }
}
